sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
    const getCurDate = function() {
        const date = new Date();
        return date.toString();
    };
    const date = Date();
    const prevMonth = date.getFullYear()
    + '-' + ('0' + (date.getMonth() + 1)).slice(-2)
    + '-' + '01';  // + ('0' + date.getDate()).slice(-2);
    api.setState('prevMonth', prevMonth);

    const thisMonth = date.getFullYear().toString()
    + '-' + ('0' + (date.getMonth().toString())).slice(-2);
    api.setState('thisMonth', thisMonth);

    return getCurDate();
}
