sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
    console.log(JSON.stringify(event.payload.row.rowData.value));
    const tmp = JSON.parse(JSON.stringify(event.payload.row.rowData.value));
    console.log(tmp[0][1].columnData.value);
    api.setState('selectedAppNo', tmp[0][1].columnData.value);

    console.log(api.state.selectedAppNo);
}
